
DROP DATABASE IF EXISTS high_school;
CREATE DATABASE high_school;
USE high_school;

create table student(
registration_number SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
first_name VARCHAR(45) NOT NULL,
last_name VARCHAR(45) NOT NULL,
date_of_birth DATETIME NOT NULL,


PRIMARY KEY  (registration_number)
);

create table teacher(
id SMALLINT UNSIGNED NOT NULL,
first_name VARCHAR(45) NOT NULL,
last_name VARCHAR(45) NOT NULL,
date_of_birth DATETIME NOT NULL,


PRIMARY KEY  (id)
);

create table course(
id_course SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
name VARCHAR(45) NOT NULL,
assigned_teacher SMALLINT UNSIGNED NOT NULL,
hours_week SMALLINT UNSIGNED,
schedule_time SMALLINT UNSIGNED,
date_of_assignment DATETIME NOT NULL,


PRIMARY KEY  (id_course),
FOREIGN KEY (assigned_teacher) REFERENCES teacher(id)
);

create table student_course(
register_id SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
student SMALLINT UNSIGNED NOT NULL,
course SMALLINT UNSIGNED NOT NULL,


PRIMARY KEY  (register_id),
FOREIGN KEY (student) REFERENCES student(registration_number),
FOREIGN KEY (course) REFERENCES course(id_course)
);

create table student_course_calification(
register_id SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT,
student SMALLINT UNSIGNED NOT NULL,
course SMALLINT UNSIGNED NOT NULL,
first_parcial SMALLINT UNSIGNED,
second_parcial SMALLINT UNSIGNED,
third_parcial SMALLINT UNSIGNED,
final_exam SMALLINT UNSIGNED,

PRIMARY KEY  (register_id),
FOREIGN KEY (student) REFERENCES student(registration_number),
FOREIGN KEY (course) REFERENCES course(id_course)
);

